import java.util.Arrays;import java.util.Comparator;import java.util.List;public class SorttingByLength {    public static void main(String[] args) {        List<String> list = Arrays.asList("Ram","Roshan","Raju","Akash","Jagrut","Harshal");        list.sort(Comparator.comparingInt(String::length));        System.out.println(list);    }}